import React, { Component } from "react";
import "./App.css";
import sourceData from "./data.json";
import Navbar from "./components/Navbar";
import FilterBox from "./components/FilterBox";
import ImageTable from "./components/ImageTable";

class App extends Component {
  state = {
    imageData: sourceData,
    pageStart: 0,
    pageLast: 5,
    currentPage: sourceData.slice(0, 9),
    categories: ["sports", "nature", "cars"],
    filters: {
      sports: true,
      nature: true,
      cars: true
    }
  };

  genericOnChange(event) {
    //
    const { name, checked } = event.target;
    var updatedFilter = [];
    this.setState(
      {
        pageStart: 0,
        pageEnd: 9,
        filters: Object.assign({}, this.state.filters, {
          [name]: checked
        })
      },
      () => {
        const { filters } = this.state;
        updatedFilter = Object.keys(filters)
          .filter(function(key) {
            return filters[key];
          })
          .map(String);
        this.setState(
          {
            selected: updatedFilter
          },
          () => {
            const { selected } = this.state;
            const selection = imageData.filter(oneContact => {
              return selected.includes(oneContact.filter);
            });
            console.log("selection", selection.length);

            this.setState(
              {
                selection: selection
              },
              () => {
                const { selection } = this.state;
                const page = selection.slice(0, 9);
                this.setState({
                  page: page
                });
              }
            );
          }
        );
      }
    );
  }

  render() {
    return (
      <div className="App">
        <Navbar />
        <FilterBox filters={this.state.filters} />
        <ImageTable imageData={this.state.imageData} />
      </div>
    );
  }
}

export default App;
