import React, { Component } from "react";
import { Link } from "react-router-dom";
import "./Pagination.css";

class Pagination extends Component {
  render() {
    const imageArray = this.props.imageData;
    var pageFloor = Math.floor(imageArray.length / 9);
    var pageStart = [];
    for (var i = 0; i < pageFloor; i++) {
      pageStart.push(i * 9);
    }
    const pageEnd = pageStart.map(oneIndex => {
      return oneIndex + 9;
    });
    if (imageArray.length / 9 !== 0) {
      pageStart.push(pageFloor * 9);
      pageEnd.push(pageFloor * 9 + (imageArray.length % 9));
    }
    var pageButtons = [];
    for (i = 1; i <= pageStart.length; i++) {
      pageButtons.push(i);
    }

    return (
      <div>
        <nav aria-label="Page navigation example">
          <ul className="pagination justify-content-center">
            {pageButtons.map((oneButton, index) => {
              return (
                <li className="page-item">
                  <Link
                    className="page-link"
                    to="#"
                    onClick={event =>
                      this.props.onPage(event, pageStart[index], pageEnd[index])
                    }
                  >
                    {oneButton}
                  </Link>
                </li>
              );
            })}
          </ul>
        </nav>
      </div>
    );
  }
}

export default Pagination;
