import React, { Component } from "react";
import "./App.css";
import sourceData from "./data.json";
import Navbar from "./components/Navbar";
import FilterBox from "./components/FilterBox";
import ImageTable from "./components/ImageTable";
import Pagination from "./components/Pagination";

class App extends Component {
  state = {
    imageData: sourceData,
    pageStart: 0,
    pageLast: 5,
    currentPage: sourceData.slice(0, 9),
    categories: ["sports", "nature", "cars"],
    filters: {
      sports: true,
      nature: true,
      cars: true
    }
  };

  genericOnChange(event) {
    //
    const { name, checked } = event.target;
    let updatedFilter = [];
    this.setState(
      {
        pageStart: 0,
        pageEnd: 9,
        filters: Object.assign({}, this.state.filters, {
          [name]: checked
        })
      },
      () => {
        const { filters } = this.state;
        updatedFilter = Object.keys(filters)
          .filter(function(key) {
            return filters[key];
          })
          .map(String);
        this.setState(
          {
            categories: updatedFilter
          },
          () => {
            const { categories } = this.state;
            const selection = sourceData.filter(oneContact => {
              console.log(oneContact.filter);
              return categories.includes(oneContact.filter);
            });
            console.log("selection", selection.length);

            this.setState(
              {
                selection: selection
              },
              () => {
                const { selection } = this.state;
                const page = selection.slice(0, 9);
                this.setState({
                  page: page
                });
              }
            );
          }
        );
      }
    );
  }

  genericSlicer(pageStartIndex, pageLastIndex) {
    const { imageData } = this.state;
    this.setState(
      {
        pageStart: pageStartIndex,
        pageLast: pageLastIndex,
        currentPage: imageData.slice(pageStartIndex, pageLastIndex)
      },
      () => {
        console.log("STATE", this.state);
      }
    );
  }

  render() {
    return (
      <div className="App">
        <Navbar />
        <FilterBox
          filters={this.state.filters}
          onBox={event => this.genericOnChange(event)}
        />
        <ImageTable imageData={this.state.currentPage} />
        <Pagination
          onPage={(event, pageStartIndex, pageEndIndex) =>
            this.genericSlicer(event, pageStartIndex, pageEndIndex)
          }
          imageData={this.state.imageData}
        />
      </div>
    );
  }
}

export default App;
